/** *  @(#) Watcher.java *   *   *  @author   Reginald Braithwaite-Lee *  @version  1.0**/package COM.codestorm.NAVajo;import COM.codestorm.mendel.*;import COM.codestorm.builders.*;import java.io.*;import COM.codestorm.thunks.*;public class Watcher implements java.lang.Runnable {private static final String kSleepTime = "kSleepTime";static {	Defaults.putDefault(kSleepTime,"10000");}/** Called when you create a new thread with this class.**/public void run() {	while (true) {		long n = NAVajoWindow.fileLastMod;		long f = NAVajoWindow.actualLastMod();		if ( n != f ) {			NAVajoWindow.processData();		}		try {			Thread.sleep(new Integer(Defaults.getProperty(kSleepTime)).intValue());		}		catch (Exception e) {			NAVajoWindow.instance().println(e.getMessage());		}	}}}