package COM.codestorm.mendel;public class MendelDebugger {	public static String nameOf (AbstractMember member) {		String name;		if (member == null)			return "<null>";		else {			try {				name = member.getProperty(AbstractMember.keyName);			}			catch (MendelException me) {				String packagePrefix = "codestorm.mendel.";				String memberClassName = member.getClass().getName();				name = "<" + ( memberClassName.startsWith(packagePrefix) ? memberClassName.substring(packagePrefix.length()) : memberClassName ) + ">";			}			try {				return MendelDebugger.nameOf(member.getStaticScope(MissingThrower.NoContainerThrower))+"#"+name;			}			catch (MendelException me) {				return name;			}		}	}		static int indentLevel = 0;	final static int indentDots = 3;	final static String dots = "......................................................";		public static void indent () {		indentLevel++;	}		public static void outdent () {		indentLevel--;	}		public static void println (String line) {		System.out.println(dots.substring(0,indentLevel*indentDots)+line);	}		public static void println () {		System.out.println();	}}