/** * * ValueType.java ***/package COM.codestorm.mendel;import java.util.*;public final class ValueType {public static ValueType CONSTANT = new ValueType();public static ValueType TRANSIENT = new ValueType();public static ValueType VOLATILE = new ValueType();private static int next = 0;private int number;private ValueType () {	number = ++next;}public boolean equals (Object obj) {	if ( obj instanceof ValueType )		return ((ValueType) obj).number == this.number;	else return false;}}