/* */package COM.codestorm.thunks;import COM.codestorm.mendel.*;class MemberByNameThunk implements AbstractThunk {String key;MissingMember ifMissing = null;public MemberByNameThunk (String s) {	key = s;}public MemberByNameThunk (String s, MissingMember missing) {	this(s);	ifMissing = missing;}public AbstractMember evaluate (AbstractMemberContainer scope) throws MendelException {	AbstractMember resolved = scope.getMember(key,MissingMember.Null);	if (resolved == null ) {		MissingMember ifNoParent = ( ifMissing == null )									? new MissingThrower(new MemberNotFoundException(scope,key))									: ifMissing;		AbstractMemberContainer scopeParent = (AbstractMemberContainer) scope.getParent(MissingMember.Null);		if ( scopeParent == null )			return ifNoParent.answer();		else return evaluate(scopeParent);	} else return resolved;}public String toString () {	return key;}}